import {getAllComments, getAllWorkExperiences} from "./client";
import {useState, useEffect} from "react";
import {Layout, Menu, Breadcrumb, Divider} from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined,
  FileOutlined,
  TeamOutlined,
  UserOutlined,
