import './App.css';
import {getAllComments, getAllWorkExperiences} from "./client";
import {useState, useEffect} from "react";
import {Layout, Menu, Breadcrumb, Divider} from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined,
  FileOutlined,
  TeamOutlined,
  UserOutlined,
} from '@ant-design/icons';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

const columns = [
  {
    title: 'Company',
    dataIndex: 'company',
    key: 'company'
  },
  {
    title: 'Title',
    dataIndex: 'title',
    key: 'title'
  },
  {
    title: 'Duration',
    dataIndex: 'duration',
    key: 'duration'
  },
  {
    title: 'Description',
    dataIndex: 'description',
    key: 'description'
  },
];

function App() {

  const[comments, setComments] = useState([]);                //for retrieving comments data to UI
  const[workExperiences, setWorkExperiences] = useState([]);  //for retrieving work experiences data to UI
  const[collapsed, setCollapsed] = useState(false);
  const[fetching, setFetching] = useState(true);

  const fetchComments = () =>
    getAllComments()
        .then(res => res.json())
        .then(data => {
          console.log(data);
          setComments(data);
          setFetching(false);
        })

  const fetchWorkExperiences = () =>
      getAllWorkExperiences()
          .then(res => res.json())  //res.json = Promise object
          .then(data => {           //data = response object
            console.log(data);
            setWorkExperiences(data); //fill the array of workExperiences with response-objects
            setFetching(false);
          })

  /*run as soon as page loaded*/
  useEffect(() => {
    console.log("component is mounted");
    //fetchComments()
    fetchWorkExperiences();
  }, []);

  const renderWorkExperiences = () => {
    if(fetching){
      return <Spin indicator={antIcon} /> //copied from https://ant.design/components/spin/
                                          //when it is fetching students, show loading spin
    }

    if(workExperiences.length <= 0){
      return <Empty/>;
    }
    return <Table dataSource={dataSource} columns={columns} />;
  }

  //html page layout
  return <Layout style={{ minHeight: '100vh' }}>
        <Sider collapsible collapsed={collapsed} onCollapse={setCollapsed}>
          <div className="logo" />
          <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            <Menu.Item key="1" icon={<PieChartOutlined />}>
              Resume
            </Menu.Item>
            <SubMenu key="sub1" icon={<UserOutlined />} title="User">
              <Menu.Item key="3">Tom</Menu.Item>
              <Menu.Item key="4">Bill</Menu.Item>
              <Menu.Item key="5">Alex</Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
              <Menu.Item key="6">Team 1</Menu.Item>
              <Menu.Item key="8">Team 2</Menu.Item>
            </SubMenu>
            <Menu.Item key="9" icon={<FileOutlined />}>
              Files
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="site-layout">
          <Header className="site-layout-background" style={{ padding: 0 }} />
          <Content style={{ margin: '0 16px' }}>
            <Breadcrumb style={{ margin: '16px 0' }}>
              <Breadcrumb.Item>Resume</Breadcrumb.Item>
              <Breadcrumb.Item>Conrad Lee</Breadcrumb.Item>
            </Breadcrumb>
            <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
              <Divider orientation="left">Work Experiences</Divider>
